import React, { useState, useEffect } from 'react';
import axios from 'axios';

// We are assuming Tailwind CSS is available
// because you added the script tag to `client/public/index.html`

// --- Main App Component ---
function App() {
  // State for navigation
  const [page, setPage] = useState('home');
  // State for authentication
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [authError, setAuthError] = useState('');

  // Function to set token in state and local storage
  const setAuthToken = (newToken) => {
    if (newToken) {
      localStorage.setItem('token', newToken);
    } else {
      localStorage.removeItem('token');
    }
    setToken(newToken);
  };

  // Function to render the current page
  const renderPage = () => {
    switch (page) {
      case 'home':
        return <HomePage />;
      case 'about':
        return <AboutPage />;
      case 'donors':
        return <DonorsPage />;
      case 'login':
        return <LoginPage setPage={setPage} setAuthToken={setAuthToken} authError={authError} setAuthError={setAuthError} />;
      case 'form':
        return <DonationFormPage token={token} setPage={setPage} />;
      default:
        return <HomePage />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 font-sans">
      <Navbar setPage={setPage} token={token} setAuthToken={setAuthToken} />
      <main className="container mx-auto p-6">
        {renderPage()}
      </main>
      <footer className="text-center p-4 text-gray-600 bg-gray-200 mt-10">
        Â© 2025 Blood Donation Portal
      </footer>
    </div>
  );
}

// --- Navbar Component ---
function Navbar({ setPage, token, setAuthToken }) {
  const handleLogout = () => {
    setAuthToken(null);
    setPage('home');
  };

  return (
    <nav className="bg-red-700 text-white shadow-md">
      <div className="container mx-auto px-6 py-4 flex justify-between items-center">
        <div 
          className="text-2xl font-bold cursor-pointer hover:text-red-200"
          onClick={() => setPage('home')}
        >
          BloodDonors
        </div>
        <ul className="flex space-x-6 items-center">
          {['home', 'about', 'donors'].map((pageName) => (
            <li key={pageName}>
              <button
                className="capitalize hover:text-red-200"
                onClick={() => setPage(pageName)}
              >
                {pageName}
              </button>
            </li>
          ))}
          {token ? (
            <>
              <li>
                <button
                  className="bg-red-500 px-4 py-2 rounded-md hover:bg-red-400"
                  onClick={() => setPage('form')}
                >
                  Donate Blood
                </button>
              </li>
              <li>
                <button
                  className="bg-gray-600 px-4 py-2 rounded-md hover:bg-gray-500"
                  onClick={handleLogout}
                >
                  Logout
                </button>
              </li>
            </>
          ) : (
            <li>
              <button
                className="bg-red-500 px-4 py-2 rounded-md hover:bg-red-400"
                onClick={() => setPage('login')}
              >
                Login to Donate
              </button>
            </li>
          )}
        </ul>
      </div>
    </nav>
  );
}

// --- Page: Home ---
function HomePage() {
  const [count, setCount] = useState(0);

  useEffect(() => {
    // Fetch the total donation count from the backend
    axios.get('/api/donations/count')
      .then(response => {
        setCount(response.data.count);
      })
      .catch(error => {
        console.error('Error fetching donation count:', error);
      });
  }, []); // Empty array means this runs once on mount

  return (
    <div className="bg-white p-8 rounded-lg shadow-lg text-center">
      <h1 className="text-4xl font-bold text-red-700 mb-4">Welcome to the Blood Donation Portal</h1>
      <p className="text-lg text-gray-700 mb-8">
        Your donation can save a life. Join our community of heroes.
      </p>
      <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-6 rounded-md inline-block">
        <div className="text-xl font-semibold">Total Donations Made</div>
        <div className="text-6xl font-bold mt-2">{count}</div>
      </div>
    </div>
  );
}

// --- Page: About ---
function AboutPage() {
  return (
    <div className="bg-white p-8 rounded-lg shadow-lg">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">About Us</h1>
      <p className="text-gray-700 leading-relaxed mb-4">
        This portal is dedicated to connecting blood donors with those in need. We aim to create a simple
        and efficient platform for managing blood donations and tracking our community's impact.
      </p>
      <p className="text-gray-700 leading-relaxed">
        Our mission is to ensure a steady and safe blood supply for everyone. By registering and donating,
        you become a vital part of this life-saving chain.
      </p>
    </div>
  );
}

// --- Page: Donors ---
function DonorsPage() {
  const [donors, setDonors] = useState([]);

  useEffect(() => {
    // Fetch recent donor names from the backend
    axios.get('/api/donations/recent')
      .then(response => {
        setDonors(response.data);
      })
      .catch(error => {
        console.error('Error fetching recent donors:', error);
      });
  }, []);

  return (
    <div className="bg-white p-8 rounded-lg shadow-lg">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Recent Donors</h1>
      <p className="text-gray-700 mb-6">
        Thank you to all our heroes who donated in the last week!
      </p>
      {donors.length > 0 ? (
        <ul className="list-disc list-inside space-y-2">
          {donors.map((name, index) => (
            <li key={index} className="text-gray-700 text-lg">{name}</li>
          ))}
        </ul>
      ) : (
        <p className="text-gray-600">No donations have been made in the last week. Be the first!</p>
      )}
    </div>
  );
}

// --- Page: Login/Register ---
function LoginPage({ setPage, setAuthToken, authError, setAuthError }) {
  const [isRegistering, setIsRegistering] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setAuthError(''); // Clear previous errors

    const url = isRegistering ? '/api/auth/register' : '/api/auth/login';
    
    try {
      const response = await axios.post(url, { username, password });
      setAuthToken(response.data.token); // Save the token
      setPage('form'); // Redirect to donation form
    } catch (err) {
      console.error('Authentication error:', err.response);
      setAuthError(err.response?.data?.msg || 'An error occurred. Please try again.');
    }
  };

  return (
    <div className="max-w-md mx-auto bg-white p-8 rounded-lg shadow-lg">
      <h1 className="text-3xl font-bold text-gray-800 mb-6 text-center">
        {isRegistering ? 'Register' : 'Login'}
      </h1>
      <form onSubmit={handleSubmit}>
        {authError && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
            {authError}
          </div>
        )}
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="username">
            Username
          </label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-red-500"
            required
          />
        </div>
        <div className="mb-6">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="password">
            Password
          </label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:ring-2 focus:ring-red-500"
            required
            minLength="6"
          />
        </div>
        <div className="flex flex-col items-center">
          <button
            type="submit"
            className="w-full bg-red-600 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          >
            {isRegistering ? 'Register' : 'Login'}
          </button>
          <button
            type="button"
            onClick={() => {
              setIsRegistering(!isRegistering);
              setAuthError('');
            }}
            className="mt-4 text-sm text-red-600 hover:text-red-800"
          >
            {isRegistering ? 'Already have an account? Login' : "Don't have an account? Register"}
          </button>
        </div>
      </form>
    </div>
  );
}

// --- Page: Donation Form ---
function DonationFormPage({ token, setPage }) {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    gender: 'Male',
    bloodGroup: 'A+',
    weight: '',
    phone: ''
  });
  const [message, setMessage] = useState('');
  const [isError, setIsError] = useState(false);

  // If user is not logged in, redirect to login
  if (!token) {
    return (
      <div className="bg-white p-8 rounded-lg shadow-lg text-center">
        <h2 className="text-2xl font-bold text-red-700 mb-4">Access Denied</h2>
        <p className="text-gray-700 mb-6">You must be logged in to access the donation form.</p>
        <button
          onClick={() => setPage('login')}
          className="bg-red-600 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
        >
          Go to Login
        </button>
      </div>
    );
  }

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setIsError(false);

    // Create the headers for the API request, including the auth token
    const config = {
      headers: {
        'Content-Type': 'application/json',
        'x-auth-token': token
      }
    };

    try {
      // Send data to the protected backend route
      const response = await axios.post('/api/donations', formData, config);
      
      // Show success message
      setIsError(false);
      setMessage(response.data.msg);
      
      // Clear form
      setFormData({
        name: '', age: '', gender: 'Male', bloodGroup: 'A+', weight: '', phone: ''
      });

    } catch (err) {
      // Show error message from the server
      setIsError(true);
      setMessage(err.response?.data?.msg || 'An error occurred. Please try again.');
    }
  };

  return (
    <div className="max-w-2xl mx-auto bg-white p-8 rounded-lg shadow-lg">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Donation Form</h1>
      
      {message && (
        <div 
          className={`px-4 py-3 rounded mb-6 ${isError ? 'bg-red-100 border border-red-400 text-red-700' : 'bg-green-100 border border-green-400 text-green-700'}`}
        >
          {message}
        </div>
      )}

      <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Name */}
        <div>
          <label className="block text-gray-700 font-bold mb-2" htmlFor="name">Full Name</label>
          <input type="text" name="name" id="name" value={formData.name} onChange={handleChange} className="form-input" required />
        </div>
        {/* Age */}
        <div>
          <label className="block text-gray-700 font-bold mb-2" htmlFor="age">Age</label>
          <input type="number" name="age" id="age" value={formData.age} onChange={handleChange} className="form-input" required min="18" />
        </div>
        {/* Gender */}
        <div>
          <label className="block text-gray-700 font-bold mb-2" htmlFor="gender">Gender</label>
          <select name="gender" id="gender" value={formData.gender} onChange={handleChange} className="form-input" required>
            <option>Male</option>
            <option>Female</option>
            <option>Other</option>
          </select>
        </div>
        {/* Blood Group */}
        <div>
          <label className="block text-gray-700 font-bold mb-2" htmlFor="bloodGroup">Blood Group</label>
          <select name="bloodGroup" id="bloodGroup" value={formData.bloodGroup} onChange={handleChange} className="form-input" required>
            {['A+', 'A-', 'B+', 'B-', 'AB+', 'AB-', 'O+', 'O-'].map(group => (
              <option key={group} value={group}>{group}</option>
            ))}
          </select>
        </div>
        {/* Weight */}
        <div>
          <label className="block text-gray-700 font-bold mb-2" htmlFor="weight">Weight (in kg)</label>
          <input type="number" name="weight" id="weight" value={formData.weight} onChange={handleChange} className="form-input" required min="60" />
        </div>
        {/* Phone */}
        <div>
          <label className="block text-gray-700 font-bold mb-2" htmlFor="phone">Phone Number</label>
          <input type="tel" name="phone" id="phone" value={formData.phone} onChange={handleChange} className="form-input" required />
        </div>
        
        {/* Submit Button */}
        <div className="md:col-span-2">
          <button type="submit" className="w-full bg-red-600 hover:bg-red-700 text-white font-bold py-3 px-4 rounded-lg focus:outline-none focus:shadow-outline text-lg">
            Submit Donation Details
          </button>
        </div>
      </form>

      {/* Simple style helper for form inputs */}
      <style>{`
        .form-input {
          shadow-sm appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-red-500
        }
      `}</style>
    </div>
  );
}

export default App;
